import React, { useState } from 'react';
import './Booking.css'; // Uncomment if styles are available
// import Dashboard from './Dashboard';
import axios from 'axios';

const Bookings = () => {
  const [formData, setFormData] = useState({ name: '', date: '', time: '', passengers: 1 });
  const [message, setMessage] = useState('');
  const tripPrice = 1500; // Define the trip price or fetch from server if dynamic

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Form Data:", formData); // Log form data
    try {
      const response = await axios.post('/api/bookings/book', formData);
      console.log("Server Response:", response.data); // Log server response
      setMessage(`Booking successful`, response.data);
    } catch (error) {
      console.error("Error:", error); // Log error
      const errorMessage = error.response?.data?.message || 'Booking failed. Please try again.';
      setMessage(errorMessage);
    }
  };


  return (
    <div className="dashboard">
      <main className="dashboard-content">
        {/* <Dashboard /> Uncomment if necessary */}
        <h2>Booking Trip: Mysore to Bangalore</h2>
        <p>Price per trip: â‚¹{tripPrice}</p>
        {message && <p className={message.includes('successful') ? 'success' : 'error'}>{message}</p>}
        <form onSubmit={handleSubmit} className="booking-form">
          <div>
            <label htmlFor="name">Name:</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label htmlFor="date">Date:</label>
            <input
              type="date"
              id="date"
              name="date"
              value={formData.date}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label htmlFor="time">Time:</label>
            <input
              type="time"
              id="time"
              name="time"
              value={formData.time}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label htmlFor="passengers">Number of Passengers:</label>
            <input
              type="number"
              id="passengers"
              name="passengers"
              value={formData.passengers}
              min="1"
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit">Book Now</button>
        </form>
      </main>
    </div>
  );
};

export default Bookings;
